import React, {useState, useEffect, useRef} from "react";
import WeatherListComponent from '../component/WeatherListComponent'
import { Multiselect } from 'multiselect-react-dropdown';

function FiltersComponent(props) {

    return filtersList(props)
}

    function filtersList (props) {
        //    console.log("coutnries: " + JSON.stringify(this.state.countries))
        return (
    <div className="row">
        {<input placeholder= "Id" onBlur= {event => {WeatherListComponent.onBlurEvent(event.target.value, "_id", "eq")}}></input>}
        {<textarea placeholder= "City name" onBlur= {event =>{WeatherListComponent.onBlurEvent(event.target.value, "name", "eq")}}></textarea>}
        {<Multiselect options ={props.countries} displayValue='name'  onSelect={event =>{WeatherListComponent.onBlurEvent(makeStringFromSelectedItems(event), "sys.country", "in")}}
        onRemove={event =>{WeatherListComponent.onBlurEvent(makeStringFromSelectedItems(event), "sys.country", "in")}}/>}
        {<textarea placeholder= "Latitude smaller than" onChange= {event =>{if (this.isNumber(event.target.value)) WeatherListComponent.onBlurEvent(event.target.value, "coord.lat", "lte")}}></textarea>}
        {<textarea placeholder= "Latitude bigger than" onBlur= {event =>{if (this.isNumber(event.target.value)) WeatherListComponent.onBlurEvent(event.target.value, "coord.lat", "gte")}}></textarea>}
        {<textarea placeholder= "Longitude smaller than" onBlur= {event =>{if (this.isNumber(event.target.value)) WeatherListComponent.onBlurEvent(event.target.value, "coord.lon", "lte")}}></textarea>}
        {<textarea placeholder= "Longitude bigger than" onBlur= {event =>{if (this.isNumber(event.target.value)) WeatherListComponent.onBlurEvent(event.target.value, "coord.lon", "gte")}}></textarea>}
        {<textarea placeholder= "Humidity smaller than" onBlur= {event =>{if (this.isNumber(event.target.value)) WeatherListComponent.onBlurEvent(event.target.value, "weatherMain.humidity", "lte")}}></textarea>}
        {<textarea placeholder= "Humidity bigger than" onBlur= {event =>{if (this.isNumber(event.target.value)) WeatherListComponent.onBlurEvent(event.target.value, "weatherMain.humidity", "gte")}}></textarea>}
        {<textarea placeholder= "Feel temperature smaller than" onBlur= {event =>{if (this.isNumber(event.target.value)) WeatherListComponent.onBlurEvent(event.target.value, "weatherMain.feels_like", "lte")}}></textarea>}
        {<textarea placeholder= "Feel temperature bigger than" onBlur= {event =>{if (this.isNumber(event.target.value)) WeatherListComponent.onBlurEvent(event.target.value, "weatherMain.feels_like", "gte")}}></textarea>}
        {<textarea placeholder= "Temperature smaller than" onBlur= {event =>{if (this.isNumber(event.target.value)) WeatherListComponent.onBlurEvent(event.target.value, "weatherMain.temp", "lte")}}></textarea>}
        {<textarea placeholder= "Temperature bigger than" onBlur= {event =>{if (this.isNumber(event.target.value)) WeatherListComponent.onBlurEvent(event.target.value, "weatherMain.temp", "gte")}}></textarea>}
        {<textarea placeholder= "Temperature max smaller than" onBlur= {event =>{if (this.isNumber(event.target.value)) WeatherListComponent.onBlurEvent(event.target.value, "weatherMain.temp_max", "lte")}}></textarea>}
        {<textarea placeholder= "Temperature max bigger than" onBlur= {event =>{if (this.isNumber(event.target.value)) WeatherListComponent.onBlurEvent(event.target.value, "weatherMain.temp_max", "gte")}}></textarea>}
        {<textarea placeholder= "Temperature min smaller than" onBlur= {event =>{if (this.isNumber(event.target.value)) WeatherListComponent.onBlurEvent(event.target.value, "weatherMain.temp_min", "lte")}}></textarea>}
        {<textarea placeholder= "Temperature min bigger than" onBlur= {event =>{if (this.isNumber(event.target.value)) WeatherListComponent.onBlurEvent(event.target.value, "weatherMain.temp_min", "gte")}}></textarea>}
        {<Multiselect options ={props.descriptions} displayValue='name'  onSelect={event =>{WeatherListComponent.onBlurEvent(makeStringFromSelectedItems(event), "weather.description", "in")}}
        onRemove={event =>{WeatherListComponent.onBlurEvent(makeStringFromSelectedItems(event), "weather.description", "in")}}/>}
    </div>)
}

// this closure's purpose is to create strings to be sent to query params, as no 
// other way to send arrays exists
function makeStringFromSelectedItems (items) {
    var selectedItemsIntoString = (prevVal, currVal, idx) => {
        return idx === 0 ? currVal.name : prevVal + "," + currVal.name
    }
    return items.reduce(selectedItemsIntoString, '')
}

export default FiltersComponent
