import React, { Component } from 'react'
import WeatherService from '../service/WeatherService';

class WeatherListComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            weathers: [],
            isAscending: true,
            filters: []
        }
    }

    componentDidMount() {
        this.refreshWeathers();
    }

    refreshWeathers(sortBy, isAscending, filterOperator, weathers) {
        console.log(isAscending + " " +  this.state.isAscending)
        // console.log("filters: " + " " +  filters)

        WeatherService.retrieveAllWeathers(sortBy, isAscending, this.state.filters, filterOperator, weathers)
            .then(
                response => {
                    console.log("response headers: " + JSON.stringify(response.headers));

                    this.setState({ weathers: response.data })
                    // console.log("response data: " + JSON.stringify(response.data));

                }
            ).then( () => {if (sortBy) this.setState({isAscending : !isAscending})})
    }

    onFilterBlur(event, filterName, filterOperator){
        if (event){ 
            this.state.filters.forEach( item => {
                if (item.has(filterName)){
                    this.state.filters.push({filterName: event.target.value}); 
                    console.log("som tu. item: " + item)
                }})
        this.refreshWeathers(this.state.sortBy, this.state.isAscending, filterOperator, this.state.weathers)
        }
    }

   filters() {
        return (<div className="row">
        {<textarea placeholder= "Id" onBlur= {event =>{ this.onFilterBlur(event, "id", "eq")}}></textarea>}
        {<textarea placeholder= "City name" onBlur= {event => { this.onFilterBlur(event, "name", "eq")}}></textarea>}
        {<textarea placeholder= "Country" onBlur= {event => { this.onFilterBlur(event, "country", "eq")}}></textarea>}
        {<textarea placeholder= "Latitude smaller than" onBlur= {event => { this.onFilterBlur(event, "lat", "lte")}}></textarea>}
        {<textarea placeholder= "Latitude bigger than" onBlur= {event => { this.onFilterBlur(event, "lat", "gte")}}></textarea>}
        {<textarea placeholder= "Longitude smaller than" onBlur= {event => { this.onFilterBlur(event, "lon", "lte")}}></textarea>}
        {<textarea placeholder= "Longitude bigger than" onBlur= {event => { this.onFilterBlur(event, "lon", "gte")}}></textarea>}
        {<textarea placeholder= "Humidity smaller than" onBlur= {event => { this.onFilterBlur(event, "humidity", "lte")}}></textarea>}
        {<textarea placeholder= "Humidity bigger than" onBlur= {event => { this.onFilterBlur(event, "humidity", "gte")}}></textarea>}
        {<textarea placeholder= "Feel temperature smaller than" onBlur= {event => { this.onFilterBlur(event, "feels_like", "lte")}}></textarea>}
        {<textarea placeholder= "Feel temperature bigger than" onBlur= {event => { this.onFilterBlur(event, "feels_like", "gte")}}></textarea>}
        {<textarea placeholder= "Temperature smaller than" onBlur= {event => { this.onFilterBlur(event, "temp", "lte")}}></textarea>}
        {<textarea placeholder= "Temperature bigger than" onBlur= {event => { this.onFilterBlur(event, "temp", "gte")}}></textarea>}
        {<textarea placeholder= "Temperature max smaller than" onBlur= {event => { this.onFilterBlur(event, "temp_max", "lte")}}></textarea>}
        {<textarea placeholder= "Temperature max bigger than" onBlur= {event => { this.onFilterBlur(event, "temp_max", "gte")}}></textarea>}
        {<textarea placeholder= "Temperature min smaller than" onBlur= {event => { this.onFilterBlur(event, "temp_min", "lte")}}></textarea>}
        {<textarea placeholder= "Temperature min bigger than" onBlur= {event => { this.onFilterBlur(event, "temp_min", "gte")}}></textarea>}
        {<textarea placeholder= "Description" onBlur= {event => { this.onFilterBlur(event, "description", "eq")}}></textarea>}
    </div>)
   }

   header(){
       return (<thead>
       <tr>
           <th onClick={() =>this.refreshWeathers("id", this.state.isAscending, '', this.state.weathers) }>cityId</th>
           <th onClick={() =>this.refreshWeathers("name", this.state.isAscending, '', this.state.weathers) }>city Name</th>
           <th onClick={() =>this.refreshWeathers("coord.lat", this.state.isAscending, '', this.state.weathers) }>latitude</th>
           <th onClick={() =>this.refreshWeathers("coord.lon", this.state.isAscending, '', this.state.weathers) }>longitude</th>
           <th onClick={() =>this.refreshWeathers("sys.country", this.state.isAscending, '', this.state.weathers) }>country</th>
           <th onClick={() =>this.refreshWeathers("weatherMain.humidity", this.state.isAscending, '', this.state.weathers) }>humidity</th>
           <th onClick={() =>this.refreshWeathers("weatherMain.feels_like", this.state.isAscending, '', this.state.weathers) }>feels like</th>
           <th onClick={() =>this.refreshWeathers("weatherMain.temp", this.state.isAscending, '', this.state.weathers) }>temperature</th>
           <th onClick={() =>this.refreshWeathers("weatherMain.temp_max", this.state.isAscending, '', this.state.weathers) }>maximum temperature</th>
           <th onClick={() =>this.refreshWeathers("weatherMain.temp_min", this.state.isAscending, '', this.state.weathers) }>minimal temperature</th>
           <th onClick={() =>this.refreshWeathers("description", this.state.isAscending, '', this.state.weathers) }>description</th>                                
       </tr>
   </thead>)
   }

   mainBody(){
    return (

    <tbody>
        {
            this.state.weathers.map(
                weather =>
                    <tr key={weather.id}>
                        <td>{weather.id}</td>
                        <td>{weather.name}</td>
                        <td>{weather.coord.lat}</td>
                        <td>{weather.coord.lon}</td>
                        <td>{weather.sys.country}</td>
                        <td>{weather.weatherMain.humidity}</td>
                        <td>{weather.weatherMain.feels_like}</td>
                        <td>{weather.weatherMain.temp}</td>
                        <td>{weather.weatherMain.temp_max}</td>
                        <td>{weather.weatherMain.temp_min}</td>
                        <td>{weather.weather[0].description}</td>

                    </tr>
            )
        }
        </tbody>
    )
   }

    render() {

        let container= [this.filters()]
        // console.log(("weathers: "  + JSON.stringify(this.state.weathers)))
        if (this.state.weathers)
            container.push(<table className="table">
            {this.header()}
            {this.mainBody()}
        </table>)

        return (
            <div className="container">
             {container}
            </div>
        )

    }
}
export default WeatherListComponent
