import React, { Component } from 'react'
import '../../styles/current/Pagination.scss'

class Pagination extends Component {
  render(){
    console.log("som v render pagination")
    const { itemsPerPage, currentPage, totalItems } = this.props;

    return (
      <nav className="pagination">
        {this.getFirstAndPrevious(totalItems, itemsPerPage, this.props, currentPage)}
        {this.getPageNumbers(totalItems, itemsPerPage, currentPage, this.props)}
        {this.getLastAndNext(totalItems, itemsPerPage, this.props, currentPage)}
      </nav>
    );
  }

  calculatePagesNumbers(itemsPerPage, totalItems){
    console.log("1 / itemsPerPage: " + totalItems*(1 / itemsPerPage))
    return (Math.ceil(totalItems *(1/ itemsPerPage) ) < 10 ? Math.ceil(totalItems *(1 / itemsPerPage) )  : 10 )
  }

  getPageNumbers(totalItems, itemsPerPage, currentPage, props){
    return (<ul className='pagination'>
    {[...new Array(Math.ceil(this.calculatePagesNumbers(itemsPerPage, totalItems))).keys()].map((number, idx, elements) => {
      return <li key={number} className='page-item'>
              <a onClick={() => {props.paginate(number+1, Math.ceil(totalItems/itemsPerPage))}} className='page-link'>
                {number+1}
              </a>
           </li>

    })
    }
  </ul>)
  }

  getFirstAndPrevious(totalItems,  itemsPerPage, currentPage,props){
    return <ul className='pagination'>
      <li key={1} className='page-item'>
        <a onClick={() => {props.paginate(1, Math.ceil(totalItems/itemsPerPage))}} className='page-link'>
          {"<<"}
        </a>
      </li>
      <li key={2} className='page-item'>
        <a onClick={() => {props.paginate(currentPage-1, Math.ceil(totalItems/itemsPerPage))}} className='page-link'>
          {"<"}
        </a>
      </li>
    </ul>
  }

  getLastAndNext(totalItems,  itemsPerPage, currentPage, props){
    return <ul className='pagination'> 
      <li key={3} className='page-item'>
        <a onClick={() => {props.paginate(currentPage+1, Math.ceil(totalItems/itemsPerPage))}} className='page-link'>
          {">"}
        </a>
      </li>

      <li key={4} className='page-item'>
        <a onClick={() => {props.paginate(Math.ceil(totalItems/itemsPerPage), Math.ceil(totalItems/itemsPerPage))}} className='page-link'>
          {">>"}
        </a>
      </li>
  </ul>
  }
}
export default Pagination
