class App extends React.Component { 

	constructor(props) {
		super(props);
		this.state = {weather: []};
	}

	componentDidMount() {
		client({method: 'GET', path: '/retrieve/fromDb'}).done(response => {
			this.setState({weather: response.entity._embedded.weather});
		});
	}

	render() {
		return (
			<WeatherList weather={this.state.weather}/>
		)
	}
}
