import i18n from 'i18next'
export const getWeatherDescription = (weather) => {
    var weatherItemReduce = (prevVal, currVal, idx) => {
        // console.log("currVal.description: " + currVal.description)
        return idx === 0 ? i18n.t(`common.description.${currVal.description}`) : (`${prevVal}`) + ", " + i18n.t(`common.description.${currVal.description}`);
    }
    return weather.weather.reduce(weatherItemReduce, '')
}

export const displayDateTime = (dateTime) => {
    return dateTime.toLocaleString("sk-SK")
}

export const convertTemperature = (convertTo, temp) => {
    // console.log(convertTo)
    if (convertTo === "celsius")
        return temp - 273.15
    else if (convertTo === "fahrenheit")
        return (temp -273.15) * 1.8 + 32
    else return temp
}

export const internationalizeDescriptions = (descriptions) => {
    return descriptions.map( (description) => (
        {"name" : i18n.t("common.description." + description.originalValue), "id": description.id, "originalValue" : description.originalValue}
    ))
}

export const internationalizeCountries = (descriptions) => {
    return descriptions.map( (country) => {
        // console.log(country)
     return {"countryName" : i18n.t("common.countryName." + country.originalCountryName), "id": country.code, "originalCountryName" : country.originalCountryName}
    })
}

export const keyExistsInArr = (arr, key) => {
    let exists = false
    if (!Array.isArray(key)){
        arr?.some(item => {
            if(item.hasOwnProperty([key])) {
                exists = true;
                return true
            }
            else return false
        })
    }
    //since countries and descriptions is a multicheckbox, they are arrays and it has multiple keys and must check not with equals
    else{
        arr?.some(item => {
            if(item.includes([key])) {
                exists = true;
                return true
            }
            else return false
        })
    }
    return exists
}

export const findIndexInFilters = (arr, key) => {
    let indexOfKey = 0
    arr?.some((filterName, index, filters) => {
        if(filterName.hasOwnProperty([key])){ 
            indexOfKey = index
            return indexOfKey
        }
        else return false
    })
    return indexOfKey
}
