import React, {Component} from 'react';

class Pagination extends Component {
    constructor(props) {
        super(props)
        this.state = {
            currentPage : 1,
            paginate : 0,
            pageNumbers : [1,2,3]
        }
        // this.fillPageNumber = this.fillPageNumber.bind()
        // this.paginate = this.paginate.bind(this)
    }

  componentDidMount() {
    console.log("comp Did mount paginate")
    this.fillPageNumber()
    console.log("pgNumbers: " + this.state.pageNumbers)
  }

  paginate = (currentPage) => {
    this.setState({currentPage : currentPage})
  }

 fillPageNumber(){ 
    for (let i = 1; i <= Math.ceil(this.props.totalItems / this.props.itemsPerPage); i++) {
      console.log("i: " + i)

      this.setState({pageNumbers : [this.state.pageNumbers, i]})
    }
      console.log("fillpageNumber: " + this.state.pageNumbers)
  }

  drawPaginationBar(){

  return (
    <nav>
      <ul className='pagination'>
        {this.state.pageNumbers.map(number => (
            // console.log("number:" + number),
          <li key={number} className='page-item'>
            <a onClick={() => {this.paginate(number)}} href='!#' className='page-link'>
              {number}
            </a>
          </li>
        ))}
      </ul>
    </nav>
  );

  }
 render() {
  console.log("Render Pagination: ")
console.log("numbers in Render: " + this.state.pageNumbers)
      console.log("currentPage: " + this.state.currentPage)
    return this.drawPaginationBar()
        }
}
export default Pagination;
