import React, { Component } from 'react'
import '../../styles/current/Pagination.css'

class Pagination extends Component {
  render(){
    console.log("som v render pagination")
    const { itemsPerPage, currentPage, totalItems } = this.props;

    return (
      <div>
      <nav className="pagination">
        <ul className='pagination'>
          {[...new Array(Math.ceil(this.calculatePagesNumbers(itemsPerPage, totalItems))).keys()].map(number => (
            <li key={number} className='page-item'>
                <a onClick={() => {this.props.paginate(number+1)}} className='page-link'>
              {number+1}
            </a>
            </li>
          ))}
          <li key=">" className='page-item'>
                <a onClick={() => {this.props.paginate(currentPage+1)}} className='page-link'>
                {">"}
          </a></li>


          <li key=">>" className='page-item'>
                <a onClick={() => {this.props.paginate(currentPage+1)}} className='page-link'>
                {">>"}
          </a></li>

        </ul>
      </nav>
      </div>
    );
  }

  calculatePagesNumbers(itemsPerPage, totalItems){
    console.log("Math.ceil(itemsPerPage / totalItems): " + Math.ceil(totalItems / itemsPerPage ))
    return (Math.ceil(totalItems / itemsPerPage ) < 10 ? Math.ceil(totalItems / itemsPerPage )  : 10 )
  }
}
export default Pagination
