import React, { Component } from 'react'
import '../../styles/current/Pagination.scss'

class Pagination extends Component {
  render(){
    console.log("som v render pagination")
    const { itemsPerPage, currentPage, totalItems } = this.props;
    const lastPage = Math.ceil(this.calculatePagesNumbers(itemsPerPage, totalItems))

    return (
      <nav className="pagination">
        {this.getFirstAndPrevious(totalItems, itemsPerPage, currentPage, lastPage, this.props)}
        {this.getPageNumbers(totalItems, itemsPerPage, currentPage, lastPage, this.props)}
        {this.getLastAndNext(totalItems, itemsPerPage, currentPage, lastPage, this.props)}
      </nav>
    );
  }

  calculatePagesNumbers(itemsPerPage, totalItems){
    console.log("1 / itemsPerPage: " + totalItems*(1 / itemsPerPage))
    return (Math.ceil(totalItems *(1/ itemsPerPage) ) < 10 ? Math.ceil(totalItems *(1 / itemsPerPage) )  : 10 )
  }

  getPageNumbers(totalItems, itemsPerPage, currentPage, lastItem, props){
    return (
      <ul className='pagination'>
      {[...new Array(lastItem).keys()].map((number, idx, elements) => {
        if (idx === 9){
          return (
            <ul className='pagination'>
              <li key="..." className='page-item'>
              <a className='page-link'>
                {"..."}
              </a>              </li>
            <li key={Math.ceil(totalItems/itemsPerPage)} className='page-item'>
              <a onClick={() => {props.paginate(Math.ceil(totalItems/itemsPerPage)+1, Math.ceil(totalItems/itemsPerPage))}} className='page-link'>
                {Math.ceil(totalItems/itemsPerPage)+1}
              </a>
            </li>
            </ul>
              )
        }
        return (
          <li key={number} className='page-item'>
            <a onClick={() => {props.paginate(number+1, Math.ceil(totalItems/itemsPerPage))}} className='page-link'>
              {number+1}
            </a>
          </li>
            )
        })
      }
    </ul>
  )
  }

  getFirstAndPrevious(totalItems, itemsPerPage, currentPage, lastItem, props){
    return (
      <ul className='pagination'>
        <li key={"first"} className='page-item'>
          <a onClick={() => {props.paginate(1, Math.ceil(totalItems/itemsPerPage))}} className='page-link'>
            {"<<"}
          </a>
        </li>
        <li key={"previous"} className='page-item'>
          <a onClick={() => {props.paginate(currentPage-1, Math.ceil(totalItems/itemsPerPage))}} className='page-link'>
            {"<"}
          </a>
        </li>
      </ul>
    )
  }

  getLastAndNext(totalItems,  itemsPerPage, currentPage, props){
    return (
      <ul className='pagination'> 
        <li key={"next"} className='page-item'>
          <a onClick={() => {props.paginate(currentPage+1, Math.ceil(totalItems/itemsPerPage))}} className='page-link'>
            {">"}
          </a>
        </li>

        <li key={"last"} className='page-item'>
          <a onClick={() => {props.paginate(Math.ceil(totalItems/itemsPerPage), Math.ceil(totalItems/itemsPerPage))}} className='page-link'>
            {">>"}
          </a>
        </li>
     </ul>
  )
  }
}
export default Pagination
