import React, {Component, useState, useEffect, useRef} from "react";
import { Multiselect } from 'multiselect-react-dropdown';
function FiltersComponent(props) {

    const [filtervalue, setFilterValue] = useState("");
    const [filterOperator, setFilterOperator] = useState("");

    useEffect(() => {
        const timeOutId = setTimeout(() => props.onChangeMethod(filtervalue, filterKey, filterOperator), 2500);
        return () => clearTimeout(timeOutId);
    }, [filterKey, filtervalue, filterOperator]);

    return (
        <div className="row">
            {<input placeholder= "Id" onChange= {event => {props.onChangeMethod(event.target.value, "_id", "eq")}}></input>}
            {<textarea placeholder= "City name" onChange= {event =>{props.onChangeMethod(event.target.value, "name", "eq")}}></textarea>}
            {<Multiselect options ={props.countries} displayValue='name'  onSelect={event =>{props.onChangeMethod(makeStringFromSelectedItems(event), "sys.country", "in")}}
            onRemove={event =>{props.onChangeMethod(makeStringFromSelectedItems(event), "sys.country", "in")}}/>}
            {<textarea placeholder= "Latitude smaller than" onChange= {event =>{if (isNumber(event.target.value)){
                setFilterKey('coord.lat')
                setFilterValue(event.target.value)
                setFilterOperator('lte')
                props.onChangeMethod(filtervalue, filterKey, filterOperator)}}}></textarea>}
            {<textarea placeholder= "Latitude bigger than" onChange= {event =>{if (isNumber(event.target.value)) props.onChangeMethod(event.target.value, "coord.lat", "gte")}}></textarea>}
            {<textarea placeholder= "Longitude smaller than" onChange= {event =>{if (isNumber(event.target.value)) props.onChangeMethod(event.target.value, "coord.lon", "lte")}}></textarea>}
            {<textarea placeholder= "Longitude bigger than" onChange= {event =>{if (isNumber(event.target.value)) props.onChangeMethod(event.target.value, "coord.lon", "gte")}}></textarea>}
            {<textarea placeholder= "Humidity smaller than" onChange= {event =>{if (isNumber(event.target.value)) props.onChangeMethod(event.target.value, "weatherMain.humidity", "lte")}}></textarea>}
            {<textarea placeholder= "Humidity bigger than" onChange= {event =>{if (isNumber(event.target.value)) props.onChangeMethod(event.target.value, "weatherMain.humidity", "gte")}}></textarea>}
            {<textarea placeholder= "Feel temperature smaller than" onChange= {event =>{if (isNumber(event.target.value)) props.onChangeMethod(event.target.value, "weatherMain.feels_like", "lte")}}></textarea>}
            {<textarea placeholder= "Feel temperature bigger than" onChange= {event =>{if (isNumber(event.target.value)) props.onChangeMethod(event.target.value, "weatherMain.feels_like", "gte")}}></textarea>}
            {<textarea placeholder= "Temperature smaller than" onChange= {event =>{if (isNumber(event.target.value)) props.onChangeMethod(event.target.value, "weatherMain.temp", "lte")}}></textarea>}
            {<textarea placeholder= "Temperature bigger than" onChange= {event =>{if (isNumber(event.target.value)) props.onChangeMethod(event.target.value, "weatherMain.temp", "gte")}}></textarea>}
            {<textarea placeholder= "Temperature max smaller than" onChange= {event =>{if (isNumber(event.target.value)) props.onChangeMethod(event.target.value, "weatherMain.temp_max", "lte")}}></textarea>}
            {<textarea placeholder= "Temperature max bigger than" onChange= {event =>{if (isNumber(event.target.value)) props.onChangeMethod(event.target.value, "weatherMain.temp_max", "gte")}}></textarea>}
            {<textarea placeholder= "Temperature min smaller than" onChange= {event =>{if (isNumber(event.target.value)) props.onChangeMethod(event.target.value, "weatherMain.temp_min", "lte")}}></textarea>}
            {<textarea placeholder= "Temperature min bigger than" onChange= {event =>{if (isNumber(event.target.value)) props.onChangeMethod(event.target.value, "weatherMain.temp_min", "gte")}}></textarea>}
            {<Multiselect options ={props.descriptions} displayValue='name'  onSelect={event =>{props.onChangeMethod(makeStringFromSelectedItems(event), "weather.description", "in")}}
            onRemove={event =>{props.onChangeMethod(makeStringFromSelectedItems(event), "weather.description", "in")}}/>}
        </div>)
}

function FiltersList (props) {
        //    console.log("coutnries: " + JSON.stringify(this.state.countries))
        const [filterKey, setFilterKey] = useState("");

}



const isNumber = (item) => {
    console.log("typeof: " + typeof item)
    console.log("isNan: " + !isNaN(item))
    var isNumber = !isNaN(item)
    if (!isNumber) alert('You must pick a number in this field')
    return isNumber
}

// this closure's purpose is to create strings to be sent to query params, as no 
// other way to send arrays exists
function makeStringFromSelectedItems (items) {
    var selectedItemsIntoString = (prevVal, currVal, idx) => {
        return idx === 0 ? currVal.name : prevVal + "," + currVal.name
    }
    return items.reduce(selectedItemsIntoString, '')
}

export default FiltersComponent
