  constructor(props){
    super(props)
    this.state={c : props.c, p: props.p, paginate: props.paginate}
  }
  render(){
    console.log("som tu v page")
      var current = this.state.c,
          last = this.state.p,
          delta = 2,
          left = current - delta,
          right = current + delta + 1,
          range = [],
          rangeWithDots = [],
          l;

      for (let i = 1; i <= last; i++) {
          if (i == 1 || i == last || i >= left && i < right) {
              range.push(i);
          }
      }

      for (let i of range) {
          if (l) {
              if (i - l === 2) {
                  rangeWithDots.push(l + 1);
              } else if (i - l !== 1) {
                  rangeWithDots.push('...');
              }
          }
          rangeWithDots.push(i);
          l = i;
      }

      return <ul>{rangeWithDots.map( item =>{
        return (
          <li> {item}</li>
        )
      } )}</ul>;

  }
}
export default Pagination
