import React, { Component } from 'react'
import '../../styles/current/Pagination.scss'

class Pagination extends Component {
  render(){
    console.log("som v render pagination")
    const { itemsPerPage, currentPage, totalItems } = this.props;

    return (
      <nav className="pagination">
      <ul>
                <li key={1} className='page-item'>
                  <a onClick={() => {this.props.paginate(1, Math.ceil(totalItems/itemsPerPage))}} className='page-link'>
                    {"<<"}
                  </a>
                </li>
                <li key={2} className='page-item'>
                  <a onClick={() => {this.props.paginate(currentPage-1, Math.ceil(totalItems/itemsPerPage))}} className='page-link'>
                    {"<"}
                  </a>
                </li>
                </ul>
        <ul className='pagination'>
          {[...new Array(Math.ceil(this.calculatePagesNumbers(itemsPerPage, totalItems))).keys()].map((number, idx, elements) => {
            // if (idx === 0){
            //   return (

            //   )
            // }
            // if (idx === 1){
            //   return (

            //   )
            // }
            // if (elements[idx+2] === 'undefined' || idx+2 === 14){
            //   return (

            //   )
            // }
            // if (elements[idx+2] === 'undefined' || idx+2 === 15){
            //   return (

            //   )
            // }
            return(
               <li key={number} className='page-item'>
                  <a onClick={() => {this.props.paginate(number+1, Math.ceil(totalItems/itemsPerPage))}} className='page-link'>
                    {number+1}
                  </a>
               </li>
            )
          })
          }
          <ul> <li key={3} className='page-item'>
                  <a onClick={() => {this.props.paginate(currentPage+1, Math.ceil(totalItems/itemsPerPage))}} className='page-link'>
                    {">"}
                  </a>
                </li>
 <li key={4} className='page-item'>
                  <a onClick={() => {this.props.paginate(Math.ceil(totalItems/itemsPerPage), Math.ceil(totalItems/itemsPerPage))}} className='page-link'>
                    {">>"}
                  </a>
                </li></ul>
        </ul>
      </nav>
    );
  }

  calculatePagesNumbers(itemsPerPage, totalItems){
    console.log("1 / itemsPerPage: " + totalItems*(1 / itemsPerPage))
    return (Math.ceil(totalItems *(1/ itemsPerPage) ) < 10 ? Math.ceil(totalItems *(1 / itemsPerPage) )  : 10 )
  }
}
export default Pagination
